import React,{useState,useEffect}from 'react';
import axios from 'axios';

export default function ClientInvoiceView() {


  const[invoice,setInvoice]=useState([]);

  useEffect(()=>{
    axios.get('')
    .then(response =>{
      setInvoice(response.data);
    })
    .catch(error=>{
      console.error('there was an error fetching the invoices!',error);
    });


  },[]);
  return (
    <div className="container custom-container">
      <h1>Invoice</h1>
          <form class="d-flex pb-4 pt-4" role="search">
          
        <button class="btn  btn-outline-success me-3" type="submit">Search</button>
        <div class="col-md-5">
        <input class="form-control  " type="search" placeholder="Invoice Id / name" aria-label="Search"/>
        </div>
        
      </form>
     <table class="table table-bordered border-primary">
      <thead class="table-light">
    <tr>
      <th scope="col">No</th>
      <th scope="col">Invoice id</th>
      <th scope="col">Company Name</th>
      <th scope="col">Creation Date</th>
      <th scope="col">Contact Name</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {invoices.map((invoice,index))}
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
      <td>Thornton</td>
      <td><a href="./serviceInvoice" class="btn btn-primary">View</a></td>
    </tr>
   
     
  </tbody>
</table>
</div>
    
  )
}
